import React, { Component, Fragment } from 'react'
import Editor from './components/Editor'
var Sketchpad = require('responsive-sketchpad');

class App extends Component {

  el = undefined;
  pad = undefined;

  componentDidMount() {


    // Initialize Sketchpad
    this.el = document.getElementById('sketchpad');
    this.pad = new Sketchpad(this.el, {
      line: {
        color: '#f44335',
        size: 5
      }
    });

    // Set line color
    this.pad.setLineColor('#f44336');

    // Set line size
    this.pad.setLineSize(10);

  }
  undocanvas() {
    this.pad.undo();
  }
  clearcanvas() {
    this.pad.clear();
  }
  render() {
    return (
      <Fragment>
        <Editor />
        <div id="sketchpad"></div>
        <button onClick={this.clearcanvas}>clear</button>
        <button onClick={this.undocanvas}>undo</button>
      </Fragment>
    )
  }
}

export default App
