import React, { Component, Fragment } from 'react'
import Editor from './components/Editor'
import Navbar from './components/Navbar';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';


var Sketchpad = require('responsive-sketchpad');

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
}));

class App extends Component {

  classes = useStyles();

  componentDidMount() {

    var el = document.getElementById('sketchpad');
    var pad = new Sketchpad(el, {
      line: {
        color: '#f44335',
        size: 5
      }
    });
    // Initialize Sketchpad


    // Set line color
    pad.setLineColor('#f44336');

    // Set line size
    pad.setLineSize(10);

  }
  undocanvas() {
    var el = document.getElementById('sketchpad');
    var pad = new Sketchpad(el, {
      line: {
        color: '#f44335',
        size: 5
      }
    });
    pad.undo();
  }
  clearcanvas() {
    var el = document.getElementById('sketchpad');
    var pad = new Sketchpad(el, {
      line: {
        color: '#f44335',
        size: 5
      }
    });
    pad.clear();
  }
  render() {
    return (
      <Fragment>
        <Navbar />
        <div className={this.classes.root} style={{ 'backgroundColor': "#f1f3f8", 'fontFamily': "poppins" }}>

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Paper className={classes.paper}>xs=12</Paper>
            </Grid>
            <Grid item xs={6}>
              <Paper className={classes.paper}>xs=6</Paper>
            </Grid>
            <Grid item xs={6}>
              <Paper className={classes.paper}>xs=6</Paper>
            </Grid>
            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
            <Grid item xs={3}>
              <Paper className={classes.paper}>xs=3</Paper>
            </Grid>
          </Grid>
          <Editor />
          <div id="sketchpad"></div>
          <button onClick={this.clearcanvas}>clear</button>
          <button onClick={this.undocanvas}>undo</button>
        </div>
      </Fragment>
    )
  }
}

export default App
