import React, { Component } from "react";
import AceEditor from "react-ace";
import "ace-builds/src-min-noconflict/ext-searchbox";
import "ace-builds/src-min-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/mode-jsx";

const languages = [
    "javascript",
    "java",
    "python",
    "xml",
    "ruby",
    "sass",
    "markdown",
    "mysql",
    "json",
    "html",
    "handlebars",
    "golang",
    "csharp",
    "elixir",
    "typescript",
    "css"
];

const themes = [
    "monokai",
    "github",
    "tomorrow",
    "kuroir",
    "twilight",
    "xcode",
    "textmate",
    "solarized_dark",
    "solarized_light",
    "terminal"
];

//importing languages
languages.forEach(lang => {
    require(`ace-builds/src-noconflict/mode-${lang}`);
    require(`ace-builds/src-noconflict/snippets/${lang}`);
});
//importing themes
themes.forEach(theme => require(`ace-builds/src-noconflict/theme-${theme}`));
S


const defaultValue = `Welcome to Syntax meets
call your friends
grab a tea
and lets begin`;

class Editor extends Component {

    onChange(newValue) {
        console.log("change", newValue);
        this.setState({
            value: newValue
        });
    }

    onSelectionChange(newValue, event) {
        console.log("select-change", newValue);
        console.log("select-change-event", event);
    }

    onCursorChange(newValue, event) {
        console.log("cursor-change", newValue);
        console.log("cursor-change-event", event);
    }

    onValidate(annotations) {
        console.log("onValidate", annotations);
    }

    setPlaceholder(e) {
        this.setState({
            placeholder: e.target.value
        });
    }
    setTheme(e) {
        this.setState({
            theme: e.target.value
        });
    }
    setMode(e) {
        this.setState({
            mode: e.target.value
        });
    }
    setBoolean(name, value) {
        this.setState({
            [name]: value
        });
    }
    setFontSize(e) {
        this.setState({
            fontSize: parseInt(e.target.value, 10)
        });
    }
    constructor(props) {
        super(props);
        this.state = {
            value: defaultValue,
            placeholder: "Placeholder Text",
            theme: "monokai",
            mode: "javascript",
            enableBasicAutocompletion: false,
            enableLiveAutocompletion: false,
            fontSize: 14,
            showGutter: true,
            showPrintMargin: true,
            highlightActiveLine: true,
            enableSnippets: false,
            showLineNumbers: true
        };
        this.setPlaceholder = this.setPlaceholder.bind(this);
        this.setTheme = this.setTheme.bind(this);
        this.setMode = this.setMode.bind(this);
        this.onChange = this.onChange.bind(this);
        this.setFontSize = this.setFontSize.bind(this);
        this.setBoolean = this.setBoolean.bind(this);
    }
    render() {
        return (
            <div className="columns">
                <div className="column">
                    <div className="field">
                        <label>Mode:</label>
                        <p className="control">
                            <span className="select">
                                <select
                                    name="mode"
                                    onChange={this.setMode}
                                    value={this.state.mode}
                                >
                                    {languages.map(lang => (
                                        <option key={lang} value={lang}>
                                            {lang}
                                        </option>
                                    ))}
                                </select>
                            </span>
                        </p>
                    </div>

                    <div className="field">
                        <label>Theme:</label>
                        <p className="control">
                            <span className="select">
                                <select
                                    name="Theme"
                                    onChange={this.setTheme}
                                    value={this.state.theme}
                                >
                                    {themes.map(lang => (
                                        <option key={lang} value={lang}>
                                            {lang}
                                        </option>
                                    ))}
                                </select>
                            </span>
                        </p>
                    </div>

                    <div className="field">
                        <label>Font Size:</label>
                        <p className="control">
                            <span className="select">
                                <select
                                    name="Font Size"
                                    onChange={this.setFontSize}
                                    value={this.state.fontSize}
                                >
                                    {[14, 16, 18, 20, 24, 28, 32, 40].map(lang => (
                                        <option key={lang} value={lang}>
                                            {lang}
                                        </option>
                                    ))}
                                </select>
                            </span>
                        </p>
                    </div>

                    <div className="field">
                        <label>Placeholder:</label>
                        <p className="control">
                            <input
                                className="input"
                                type="text"
                                onChange={this.setPlaceholder}
                                value={this.state.placeholder}
                            />
                        </p>
                    </div>

                    <div className="field">
                        <p className="control">
                            <label className="checkbox">
                                <input
                                    type="checkbox"
                                    checked={this.state.enableBasicAutocompletion}
                                    onChange={e =>
                                        this.setBoolean(
                                            "enableBasicAutocompletion",
                                            e.target.checked
                                        )
                                    }
                                />
                Enable Basic Autocomplete
              </label>
                        </p>
                    </div>
                    <div className="field">
                        <p className="control">
                            <label className="checkbox">
                                <input
                                    type="checkbox"
                                    checked={this.state.enableLiveAutocompletion}
                                    onChange={e =>
                                        this.setBoolean(
                                            "enableLiveAutocompletion",
                                            e.target.checked
                                        )
                                    }
                                />
                Enable Live Autocomplete
              </label>
                        </p>
                    </div>
                    <div className="field">
                        <p className="control">
                            <label className="checkbox">
                                <input
                                    type="checkbox"
                                    checked={this.state.showGutter}
                                    onChange={e =>
                                        this.setBoolean("showGutter", e.target.checked)
                                    }
                                />
                Show Gutter
              </label>
                        </p>
                    </div>
                    <div className="field">
                        <p className="control">
                            <label className="checkbox">
                                <input
                                    type="checkbox"
                                    checked={this.state.showPrintMargin}
                                    onChange={e =>
                                        this.setBoolean("showPrintMargin", e.target.checked)
                                    }
                                />
                Show Print Margin
              </label>
                        </p>
                    </div>
                    <div className="field">
                        <p className="control">
                            <label className="checkbox">
                                <input
                                    type="checkbox"
                                    checked={this.state.highlightActiveLine}
                                    onChange={e =>
                                        this.setBoolean("highlightActiveLine", e.target.checked)
                                    }
                                />
                Highlight Active Line
              </label>
                        </p>
                    </div>
                    <div className="field">
                        <p className="control">
                            <label className="checkbox">
                                <input
                                    type="checkbox"
                                    checked={this.state.enableSnippets}
                                    onChange={e =>
                                        this.setBoolean("enableSnippets", e.target.checked)
                                    }
                                />
                Enable Snippets
              </label>
                        </p>
                    </div>
                    <div className="field">
                        <p className="control">
                            <label className="checkbox">
                                <input
                                    type="checkbox"
                                    checked={this.state.showLineNumbers}
                                    onChange={e =>
                                        this.setBoolean("showLineNumbers", e.target.checked)
                                    }
                                />
                Show Line Numbers
              </label>
                        </p>
                    </div>
                </div>
                <div className="examples column">
